qrList.message:
// Check if ms_count is already set. |
/if left="{{getvar::ms_count}}" right="" rule=neq else={:
	// Pass 3 through the pipe if false |
	/pass 3 |
:}
{:
	// Pass ms_count's value through the pipe if true |
	/pass {{getvar::ms_count}} |

:} |
// Define scope var ms_cur to current value of ms_count (or set it to default) |
/let ms_cur {{pipe}} |

// Have user input a number between 1 and 10 (default value is set to ms_cur) |
/input default="{{var::ms_cur}}" Pick a number between 1 and 10 |
/let ms_in {{pipe}} |

// Check to make sure the value is not set over or under the limit |
/if left=ms_in right=10 rule=lte {:
	/if left=ms_in right=1 rule=gte {:
		// Set ms_count to ms_in |
		/setvar key=ms_count {{var::ms_in}}
	:}
:}

// Check if ms_count is set |
/if left="{{getvar::ms_count}}" right="" rule=neq else={:
	// Default to 3 swipes when unset |
	/times 3 {:
		/swipes-swipe |
	:} |
:}
{:
	// Swipe the amount of times ms_count is set to |
	/times {{getvar::ms_count}} {:
		/swipes-swipe |
	:} |
:}|
